# encoding: utf-8
module Faker
  class Company
    class << self

      def fr_name
		    Faker::Lorem.words(rand(1..2)).join(' ').titleize + " #{%w{SA Sarl}.sample}"
      end

      def fr_siren
        number = Faker::Base.numerify('########')
        siren = number + "#{FakerExtensionFr::Luhn.checksum(number)}"
        	
      end

      def fr_siret(siren, seq=1)
        number = siren.to_s.delete(' ') + ("%04d" % seq)
        siret = number + "#{FakerExtensionFr::Luhn.checksum(number)}"		
      end

      def fr_vat_numbert(siren)
        number = siren.to_s.delete(' ').to_i # convert to integer
        sClef = (((number % 97) *3 ) + 12 ) % 97
        sClef =  sClef < 10 ?  "0"+ sClef.to_s : sClef.to_s
        vat_number = "FR#{sClef} " + siren.to_s
      end

      def fr_rcs(area="01")
        area = "01" if Fr_rcs_registration[area.to_s].nil?
        Fr_rcs_registration[area.to_s].sample
      end

      def fr_ape(business_type=nil)
        business_type = Fr_insee_naf_sector.sample if (business_type.nil? ||  !Fr_insee_naf_sector.include?(business_type.to_s))
        ape_code = Fr_ape_code[business_type.to_s].sample
        [ape_code, Fr_ape_label[ape_code] ]
      end
      
      def fr_bank(area=nil, bank=nil)
        bank = %w{ SG LCL BNP CA Poste}.sample if bank.nil? || !%w{ SG LCL BNP CA Poste}.include?(bank)
        bank_name = Fr_banks[bank]['label']
        bank_iban = Fr_banks[bank]['IBAN']
        bank_bic = Fr_banks[bank]['BIC']
        bank_location = Faker::Address.fr_zip_and_city_in_area(area)
        bank_address = Faker::Address.fr_street_name
        [bank_name, bank_address, bank_location, bank_iban, bank_bic].flatten       
      end

      def fr_bank_account(area=nil, bank=nil)
        bank = %w{ SG LCL BNP CA Poste}.sample if bank.nil? || !%w{ SG LCL BNP CA Poste}.include?(bank)
        bank_name = Fr_banks[bank]['label']
        bank_iban = Fr_banks[bank]['IBAN']
        bank_bic = Fr_banks[bank]['BIC']
        bank_location = Faker::Address.fr_zip_and_city_in_area(area)
        bank_address = Faker::Address.fr_street_name
        account_number = Faker::Base.numerify('00000######')
        sClef = (((account_number.to_i % 97) *3 ) + 12 ) % 97
        sClef =  sClef < 10 ?  "0"+ sClef.to_s : sClef.to_s
        bank_account = account_number + " " + sClef
        bank_office = "%05d" % rand(25..555)
        iban_account_1 = "FR" + ("%02d" % rand(1..99)) 
        iban_account_2 = " #{bank_iban[0,4]} "
        iban_account_3 =  "#{bank_iban[4,1]}#{bank_office[0,3]} " 
        iban_account_4 = "#{bank_office[3,2]}#{bank_account[0,2]} "
        iban_account_5 = "#{bank_account[2,4]} "
        iban_account_6 = "#{bank_account[6,4]} "
        iban_account_7 = "#{bank_account[10,1]}#{bank_account[12,2]}"
        iban_account = iban_account_1 + iban_account_2 + iban_account_3 + iban_account_4 + iban_account_5 + iban_account_6 + iban_account_7
        bic_swift = "FR#{bank_bic}#{I18n.transliterate(bank_location[1][0,3]).upcase}"
        [bank_name, bank_address, bank_location, bank_iban, bank_office, bank_account, iban_account, bic_swift ].flatten       
      end


